import "core-js/modules/es.array.push.js";
import { ref, computed, onMounted, watch, nextTick } from 'vue';
import * as echarts from 'echarts';
import 'echarts-gl';
import * as XLSX from 'xlsx';
//   import ecStat from 'echarts-stat';

export default {
  __name: 'qnAnalysis',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const selectedPrimary = ref('');
    const selectedSecondary = ref('');
    const analysisResult = ref({});
    const surveyData = ref([{
      age: '18-25',
      occupation: 'Teacher',
      education: 'Bachelors',
      satisfaction: 'High',
      recommendation: 'Likely',
      productUsage: 'Daily'
    }, {
      age: '18-25',
      occupation: 'Student',
      education: 'Bachelors',
      satisfaction: 'Mid',
      recommendation: 'Likely',
      productUsage: 'Daily'
    }, {
      age: '18-25',
      occupation: 'Student',
      education: 'Bachelors',
      satisfaction: 'Mid',
      recommendation: 'Likely',
      productUsage: '4 days a week'
    }, {
      age: '25-32',
      occupation: 'Student',
      education: 'Doctor',
      satisfaction: 'High',
      recommendation: 'Likely',
      productUsage: 'Daily'
    }, {
      age: '18-25',
      occupation: 'Teacher',
      education: 'Doctor',
      satisfaction: 'High',
      recommendation: 'Likely',
      productUsage: 'Daily'
    }, {
      age: '25-32',
      occupation: 'Teacher',
      education: 'Bachelors',
      satisfaction: 'High',
      recommendation: 'Likely',
      productUsage: 'Daily'
    }, {
      age: '25-32',
      occupation: 'Student',
      education: 'Bachelors',
      satisfaction: 'Low',
      recommendation: 'Likely',
      productUsage: '2 days per week'
    }, {
      age: '18-25',
      occupation: 'Student',
      education: 'Bachelors',
      satisfaction: 'High',
      recommendation: 'Unlikely',
      productUsage: 'Daily'
    }, {
      age: '10-18',
      occupation: 'Student',
      education: 'Bachelors',
      satisfaction: 'Mid',
      recommendation: 'Likely',
      productUsage: '3 days per week'
    }]);
    const exportToExcel = () => {
      const wb = XLSX.utils.book_new();
      const ws = XLSX.utils.json_to_sheet(formattedTableData.value);
      XLSX.utils.book_append_sheet(wb, ws, 'AnalysisResult');
      XLSX.writeFile(wb, 'analysis_result.xlsx');
    };
    let myChart;
    const exportChart = () => {
      if (myChart) {
        const img = new Image();
        const url = myChart.getDataURL({
          pixelRatio: 2,
          backgroundColor: '#fff'
        });
        img.src = url;
        // 创建下载链接并点击进行下载
        const a = document.createElement('a');
        a.download = 'chart.png';
        a.href = url;
        a.click();
      }
    };
    const questionsData = computed(() => {
      if (!surveyData.value.length) return [];
      const questionNames = Object.keys(surveyData.value[0]);
      return questionNames.map(name => ({
        name,
        displayName: name.charAt(0).toUpperCase() + name.slice(1)
      }));
    });
    onMounted(() => {
      if (questionsData.value.length > 0) {
        selectedPrimary.value = questionsData.value[0].name;
        selectedSecondary.value = questionsData.value.length > 1 ? questionsData.value[1].name : questionsData.value[0].name;
      }
    });
    const formattedTableData = computed(() => {
      let tableData = [];
      if (analysisResult.value && Object.keys(analysisResult.value).length) {
        Object.keys(analysisResult.value).forEach(primary => {
          Object.keys(analysisResult.value[primary]).forEach(secondary => {
            tableData.push({
              primary: getDisplayName(primary),
              secondary: getDisplayName(secondary),
              count: analysisResult.value[primary][secondary]
            });
          });
        });
      }
      return tableData;
    });
    const performAnalysis = () => {
      const primaryQuestion = selectedPrimary.value;
      const secondaryQuestion = selectedSecondary.value;
      let analysisMap = new Map();
      surveyData.value.forEach(response => {
        const primaryResponse = response[primaryQuestion];
        const secondaryResponse = response[secondaryQuestion];
        if (!analysisMap.has(primaryResponse)) {
          analysisMap.set(primaryResponse, {});
        }
        const secondaryCounts = analysisMap.get(primaryResponse);
        secondaryCounts[secondaryResponse] = (secondaryCounts[secondaryResponse] || 0) + 1;
      });
      let result = {};
      analysisMap.forEach((value, key) => {
        result[key] = value;
      });
      analysisResult.value = result;

      // Ensure the chart updates with the new analysis result
      nextTick(() => {
        if (Object.keys(analysisResult.value).length > 0) {
          setChart();
        }
      });
    };
    const getDisplayName = questionName => {
      const question = questionsData.value.find(q => q.name === questionName);
      return question ? question.displayName : questionName;
    };
    const setChart = () => {
      nextTick(() => {
        const chartDom = document.getElementById('chart');
        if (chartDom && analysisResult.value) {
          myChart = echarts.init(chartDom);
          const dimensions = {
            x: '',
            y: '',
            z: ''
          };
          let data = [];
          let xData = [];
          let yData = [];
          Object.keys(analysisResult.value).forEach(x => {
            Object.keys(analysisResult.value[x]).forEach(y => {
              data.push([x, y, analysisResult.value[x][y]]);
              if (xData.indexOf(x) === -1) xData.push(x);
              if (yData.indexOf(y) === -1) yData.push(y);
            });
          });
          dimensions.x = selectedPrimary.value;
          dimensions.y = selectedSecondary.value;
          dimensions.z = 'Count';
          let option = {
            tooltip: {},
            visualMap: {
              max: 20,
              inRange: {
                color: ['#313695', '#4575b4', '#74add1', '#abd9e9', '#e0f3f8', '#ffffbf', '#fee090', '#fdae61', '#f46d43', '#d73027', '#a50026']
              }
            },
            xAxis3D: {
              type: 'category',
              data: xData,
              name: dimensions.x,
              axisLabel: {
                formatter: function (value) {
                  return value;
                }
              }
            },
            yAxis3D: {
              type: 'category',
              data: yData,
              name: dimensions.y
            },
            zAxis3D: {
              type: 'value',
              name: dimensions.z
            },
            grid3D: {
              boxWidth: 200,
              boxDepth: 80,
              viewControl: {
                // projection: 'orthographic'
              },
              light: {
                main: {
                  intensity: 1.2,
                  shadow: true
                },
                ambient: {
                  intensity: 0.3
                }
              }
            },
            series: [{
              type: 'bar3D',
              data: data.map(function (item) {
                return {
                  value: [item[0], item[1], item[2]]
                };
              }),
              shading: 'lambert',
              label: {
                textStyle: {
                  fontSize: 16,
                  borderWidth: 1
                }
              },
              emphasis: {
                label: {
                  textStyle: {
                    fontSize: 20,
                    color: '#900'
                  }
                },
                itemStyle: {
                  color: '#900'
                }
              }
            }]
          };
          myChart.setOption(option);
        }
      });
    };
    watch(analysisResult, newVal => {
      if (newVal) {
        setChart();
      }
    });
    const __returned__ = {
      selectedPrimary,
      selectedSecondary,
      analysisResult,
      surveyData,
      exportToExcel,
      get myChart() {
        return myChart;
      },
      set myChart(v) {
        myChart = v;
      },
      exportChart,
      questionsData,
      formattedTableData,
      performAnalysis,
      getDisplayName,
      setChart,
      ref,
      computed,
      onMounted,
      watch,
      nextTick,
      get echarts() {
        return echarts;
      },
      get XLSX() {
        return XLSX;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
