        // 问题关联检查
        if (this.willAddQuestion.id !== 0 && this.sonGroup(this.willAddQuestion.id - 1).length !== 0) {
          this.$message({
            type: 'warning',
            message: '此问题已被关联，无法删除选项，请删除关联信息后再试'
          });
          return;
