const key = 'userData'
const user = {
    state(){
        return {
            userData: null
        }
    },
    getters: {
        getUserData: function(state){
            if(!state.userData){
                state.userData = JSON.parse(localStorage.getItem(key))
            }
            return state.user
        }
    },
    mutations: {
        setStorage(state, value){
            state.userData = value;
            localStorage.setItem(key,JSON.stringify(value))
        },
        removeStorage(state){
            state.userData = null;
            localStorage.removeItem(key)
        }
    },
    actions: {
        saveUserInfo({ commit },data){
            commit('setStorage', data)
        },
        deleteUserInfo({ commit },data){
            commit('removeStorage', data)           
        }
    }
};

export default user
